// UI5 Web Components JSX intrinsic element declarations
// Keeps TypeScript from complaining about unknown custom elements.
// For stronger typing, map attributes as needed later.
declare namespace JSX {
  interface IntrinsicElements {
    'ui5-table': any;
    'ui5-table-column': any;
    'ui5-table-row': any;
    'ui5-table-cell': any;
    'ui5-badge': any;
  }
}

